jQuery.extend({
    passwordsVerify:function(pass){
        //四到六位数字或者字母
        //为空返回-2
        //小于6位或者大于16位返回-1
        //不合法返回0
        //合法返回1
        var patrn=/[^\w]$/;
        if(pass==""){
            return -2;
        }
        if(pass.length<6||pass.length>16){
            return -1;
        }
        else {
            var result = patrn.test(pass);
            if(result){
                return 0;
            }
            return 1;
        }

    },

    checkMobile:function (str) {

        var re = /^1[3|4|5|7|8]\d{9}$/;

        if (re.test(str)) {
            return true;
        } else {
            return false;
        }
    }

});